import{f as e}from"index-939d0dd8.js"/*tpa=https://cdn.w3cbus.com/mdui/design-1/assets/index-939d0dd8.js*/;const t={a1:"Pickers provide a simple way to select a single value from a pre-determined set.",a2:"<strong>Date pickers</strong> use a dialog window to select a single date on mobile.",a3:"<strong>Time pickers</strong> use a dialog to select a single time (in the hours:minutes format) on mobile. They adjust to a user’s preferred time setting.",a4:"Alternatives",a5:"Segmented dropdown buttons",a6:"Usage",a7:"Date pickers",a8:"Time pickers",a9:`On mobile, pickers are best suited for display in a <a class="do-router" href="${e("/components/dialogs.html#dialogs-confirmation-dialogs")}">confirmation dialog</a>.`,a10:`For inline display, such as on a form, consider using compact controls such as <a class="do-router" href="${e("/components/buttons.html#buttons-other-buttons")}">segmented dropdown buttons</a>.`,a11:"A dialog picker is used to select a single date on mobile.",a12:"The selected day is indicated by a filled circle. The current day is indicated by a different color and type weight.",a13:"Date and year picker: Light theme, portrait",a14:"Swipe left to right to navigate through the months. Touch the year in the title bar to transition to the year view.",a15:"Date picker navigation",a16:"The picker view adapts to the device’s orientation.",a17:"Date picker: Light theme, landscape",a18:"A dark theme picker can complement apps that have a dark theme.",a19:"Date picker: Dark theme, portrait",a20:"Landscape view of the dark theme picker.",a21:"Date picker: Dark theme, landscape",a22:"A time picker adjusts to a user’s preferred time setting, i.e. the 12-hour or 24-hour format.",a23:"A dialog picker is used to select a single time (hours:minutes) on mobile.",a24:"The selected time is indicated by the filled circle at the end of the clock hand.",a25:"Time picker: Light theme, portrait",a26:"Tap between the hour and minute label in the title bar to switch between the hour or minute view. Additionally, tap on the AM/PM label in the title bar to switch between AM or PM (in 12-hour clock mode).",a27:"Time picker navigation",a28:"The picker view adapts to the device’s orientation.",a29:"Time picker: Light theme, landscape",a30:"A dark theme picker can complement apps that have a dark theme.",a31:"Time picker: Dark theme, portrait",a32:"Landscape view of the dark theme picker",a33:"Time picker: Dark theme, landscape",a34:"Material Design pickers for selecting single values",a35:"Material Design date and year picker in light theme",a36:"Material Design Date Picker: Light Theme, Landscape",a37:"Material Design Date Picker in Dark Theme",a38:"Material Design date picker in dark theme, landscape",a39:"Material Design time picker in light theme, portrait",a40:"Material Design Time Picker: Light Theme, Landscape",a41:"Material Design dark theme time picker",a42:"Material Design Time Picker: Dark Theme, Landscape"};export{t as en};
