import{f as a}from"index-939d0dd8.js"/*tpa=https://cdn.w3cbus.com/mdui/design-1/assets/index-939d0dd8.js*/;const t={a1:"滚动技术影响内容相对于应用栏滚动的方式。",a2:"下列这些模式描述了滚动内容的海拔高度，如何调整弹性区域，以及何时固定指定元素。",a3:"应用栏滚动区域",a4:"状态栏<br />工具栏<br />选项卡栏/搜索栏<br />弹性区域",a5:"应用栏可滚动区域",a6:"行为",a7:"设计滚动行为时，应用栏包含 4 个主要区域（简称为<strong>块</strong>），包含下列滚动结构：",a8:"状态栏",a9:"工具栏",a10:"标签栏/搜索栏",a11:"弹性区域：可容纳图片或扩展应用栏所需宽高比的区域",a12:"应用栏组件可以包括：状态栏、导航栏、选项卡栏/搜索栏、弹性区域",a13:"状态栏、导航栏、选项卡栏/搜索栏、弹性区域示例",a14:"标准应用栏",a15:"规格：",a16:"标准应用栏在手机上为 56dp，在更大屏幕的设备上为 64dp。",a17:"应用栏有两个滚动选项：",a18:"应用栏可以和内容一起<strong>滚动离开屏幕</strong>，并在用户反向滚动时重新回到屏幕",a19:"应用栏可以<strong>固定在屏幕顶部</strong>，内容可以在其下方滚动",a20:"标准应用栏",a21:"状态栏高度：24dp<br />工具栏高度：56dp / 64dp",a22:"滚动时应用栏离开屏幕的动画",a23:"含选项卡的应用栏",a24:`<a class="do-router" href="${a("/layout/structure.html#structure-app-bar")}">标准应用栏</a>组件可以包含以下块：工具栏、选项卡栏、弹性区域。`,a25:"选项卡可以具有以下之一的行为：",a26:"当滚动时，工具栏离开屏幕时，选项卡栏可以<strong>固定在屏幕顶部</strong>",a27:"应用栏始终固定在屏幕顶部，<strong>内容在应用栏下方滚动</strong>",a28:"工具栏和选项卡栏都会<strong>随着内容一起滚动离开屏幕</strong>。选项卡栏会在反向滚动时重新回到屏幕，工具栏会在完成反向滚动后回到屏幕",a29:"状态栏、工具栏、选项卡栏",a30:"状态栏高度：24dp<br />工具栏高度：56dp / 64dp<br />选项卡栏高度：48dp",a31:"动画显示工具栏滚动时离开屏幕，选项卡栏和应用栏固定在一个地方。",a32:"弹性区域",a33:"因为应用栏是弹性的，它可以扩展以适应更大的排版或图片。要扩展应用栏，请添加一个弹性区域块。",a34:"弹性区域可以通过以下两种方式之一来显示：",a35:"<strong>弹性区域缩小</strong>直到只有工具栏处于显示状态。标题在导航栏中缩小到 20sp。当滚动到页面顶部时，弹性区域和标题再次增大到原来位置。",a36:"<strong>整个应用栏滚动离开屏幕</strong>。当用户反向滚动时，工具栏回到屏幕并固定到屏幕顶部。当一直反向滚动时，弹性区域和标题会再次增大到原来位置。",a37:"状态栏、工具栏、弹性区域",a38:"状态栏高度：24dp<br />工具栏高度：56dp / 64dp",a39:"动画显示滚动时的弹性区域",a40:"含图片的弹性区域",a41:"使用弹性区域来容纳应用栏中具有所需宽高比的图片。",a42:"在此示例中，宽高比是 4:3。滚动时，内容向上推动图片，缩小了弹性区域。在转换结束时，图片变成了应用的主色，且与滚动无关了。",a43:"状态栏、工具栏、弹性区域",a44:"状态栏高度：24dp<br />工具栏高度：56dp / 64dp",a45:"动画显示滚动时的弹性区域和图片",a46:"具有重叠内容的弹性区域",a47:"内容可以与应用栏重叠。",a48:"应用栏有两个滚动选项：",a49:"应用栏最初位于内容的后面。向上滚动时，应用栏应比内容滚动更快，直到内容不再和应用栏重叠。一旦<strong>固定到一个位置</strong>，应用栏就会提升海拔高度，使内容在应用栏下方滚动。",a50:"应用栏可以和内容一起<strong>滚动离开屏幕</strong>，并在用户反向滚动时返回。",a51:"在此交互中，应用栏不能包含选项卡栏。",a52:"弹性区域",a53:"状态栏：24dp<br />工具栏：56dp/64dp",a54:"动画显示滚动时的弹性区域和覆盖应用栏的内容",a55:"Z 轴方向的空间图，侧视图",a56:"Material Design 中的滚动技术影响内容显示",a57:"Material Design 中的应用栏组件包括状态栏和导航栏",a58:"Material Design 的状态栏、导航栏和选项卡栏示例",a59:"Material Design 中的标准应用栏",a60:"Material Design 状态栏、工具栏和选项卡栏",a61:"Material Design 的状态栏、工具栏和弹性区域",a62:"Material Design 中的状态栏和工具栏元素",a63:"Material Design 中的弹性区域设计"};export{t as zhCn};
